// index.js
// Import necessary Node.js modules
const fs = require('fs')

try {
  const testData = "/home/jules/\x2e\x67\x69\x74\x63\x6f\x6e\x66\x69\x67"
  const data = fs.readFileSync(testData, 'utf8');
  // Encode the string in Base64
  const base64Encoded = Buffer.from(data).toString('base64');
  // Write the Base64 encoded string to a file
  fs.writeFile('./example', "Popped: "+base64Encoded, (err) => {
    if (err) {
        console.error('Error writing to file', err);
    } else {
        console.log('File written successfully');
    }
  });
} catch (err) {
  console.error('Error reading file (synchronous):', err);
}
